	public static Supplier<Block> registerBlock(String name, Supplier<Block> blockSupplier) {
		Block block = Registry.register(Registries.BLOCK, new Identifier(LuckyTNTMod.MODID, name), blockSupplier.get());
		return () -> block;
	}
	
